mapper.update:
UPDATE tbl_member
SET MEMBER_NICKNAME = #{memberNickname}, MEMBER_PHONE_NUM = #{memberPhoneNum}, MEMBER_BIRTHDATE = #{memberBirthdate}, MEMBER_TEACHING_LANG = #{memberTeachingLang}, MEMBER_LEARNING_LANG = #{memberLearningLang}, MEMBER_INTERESTING = #{memberInteresting}, MEMBER_INTRODUCE = #{memberIntroduce},member_nation_url = #{memberNationUrl}
WHERE MEMBER_NUMBER = #{memberNumber}

UPDATE tbl_file_profile
SET FILE_NAME = #{fileName}, FILE_UPLOAD_PATH = #{fileUploadPath}, FILE_UUID = #{fileUuid}, FILE_IMAGE_CHECK = #{fileImageCheck}, FILE_SIZE = #{fileSize}, MEMBER_NUMBER = #{memberNumber}
WHERE FILE_NUMBER = #{fileNumber}
